(14, 'BS 04', NULL, NULL, '<div class="swiper mySwiper relative bg-white shadow-xl max-h-96">\n			<!-- Additional required wrapper -->\n			<div class="swiper-wrapper">\n				<!-- Slides -->\n				<div class="swiper-slide bg-center bg-cover h-96 flex justify-center" style="background-image: url(''img/slider-1.jpg'');">\n					<div class="inline-flex flex-col justify-center items-center h-full w-full bg-black/60 text-center">\n						<h2 class="lg:w-1/2 uppercase text-lg sm:text-2xl lg:text-5xl font-semibold text-white mb-4 sm:mb-6 lg:mb-0"><strong class="text-yellow-300">Creativity</strong> is the way of seeing things.</h2>\n						<a href="#" class="hidden md:inline-block border-2 border-white-500 text-xs sm:text-sm md:text-md font-bold text-white mt-12 px-4 py-2 transition duration-300 ease-in-out hover:bg-white hover:text-indigo-600">Read More</a>\n					</div>\n				</div>\n				<div class="swiper-slide bg-center bg-cover h-96 flex justify-center" style="background-image: url(''img/slider-2.jpg'');">\n					<div class="inline-flex flex-col justify-center items-center h-full w-full bg-black/60 text-center">\n						<h2 class="lg:w-1/2 uppercase text-lg sm:text-2xl lg:text-5xl font-semibold text-white mb-4 sm:mb-6 lg:mb-0"><strong class="text-yellow-300">Inspiration</strong> is only the first step.</h2>\n						<a href="#" class="hidden md:inline-block border-2 border-white-500 text-xs sm:text-sm md:text-md font-bold text-white mt-12 px-4 py-2 transition duration-300 ease-in-out hover:bg-white hover:text-indigo-600">Read More</a>\n					</div>\n				</div>\n				<div class="swiper-slide bg-center bg-cover h-96 flex justify-center" style="background-image: url(''img/slider-3.jpg'');">\n					<div class="inline-flex flex-col justify-center items-center h-full w-full bg-black/60 text-center">\n						<h2 class="lg:w-1/2 uppercase text-lg sm:text-2xl lg:text-5xl font-semibold text-white mb-4 sm:mb-6 lg:mb-0">Creative <strong class="text-yellow-300">thinking</strong> & <strong class="text-yellow-300">design</strong>.</h2>\n						<a href="#" class="hidden md:inline-block border-2 border-white-500 text-xs sm:text-sm md:text-md font-bold text-white mt-12 px-4 py-2 transition duration-300 ease-in-out hover:bg-white hover:text-indigo-600">Read More</a>\n					</div>\n				</div>\n			</div>\n			<!-- If we need pagination -->\n			<div class="swiper-pagination"></div>\n		  \n			<!-- If we need navigation buttons -->\n			<div class="swiper-button-prev"></div>\n			<div class="swiper-button-next"></div>\n		</div>', 2, '2022-08-24 03:35:43', '2022-08-25 05:42:51', 27, NULL, NULL, 0, NULL),
(26, 'wdt-top 01', NULL, NULL, '<!--article僅作為展示用，嵌入代碼時請刪除，以內部div為准-->\n<article class="relative mt-4 bg-white w-full h-32 shadow-xl border mb-12">\n<!--嵌入代碼時，請將absolute修改為fixed;-->\n<div class="absolute bottom-4 right-4 z-50">\n<button class="flex justify-center items-center w-12 h-12 bg-black opacity-30 hover:bg-indigo-500 hover:opacity-100 text-white rounded" x-data\n  @click.window="window.scrollTo({top: 0, behavior: ''smooth''})"\n>\n<i class="fas fa-angle-up"></i>\n</button>\n</div>\n</article>', 6, '2022-08-28 07:16:27', '2022-09-06 01:32:54', 100, NULL, NULL, 0, NULL);
INSERT INTO `module` (`id`, `title`, `user_id`, `imgurl`, `html`, `moduletp_id`, `created_at`, `updated_at`, `sort_order`, `meta_keyword`, `meta_description`, `ishome`, `imgs`) VALUES
(28, 'lightbox', NULL, NULL, '<article class="relative mt-4 bg-white w-full  shadow-xl border">\n			<link href="https://unpkg.com/tailwindcss@^1.0/dist/tailwind.min.css" rel="stylesheet">\n    		<script src="https://cdn.jsdelivr.net/gh/alpinejs/alpine@v2.x.x/dist/alpine.min.js" defer></script>\n			\n    		<div x-data="{ imgModal : false, imgModalSrc : '''', imgModalDesc : '''' }">\n		        <template @img-modal.window="imgModal = true; imgModalSrc = $event.detail.imgModalSrc; imgModalDesc = $event.detail.imgModalDesc;" x-if="imgModal">\n		          <div x-transition:enter="transition ease-out duration-300" x-transition:enter-start="opacity-0 transform scale-90" x-transition:enter-end="opacity-100 transform scale-100" x-transition:leave="transition ease-in duration-300" x-transition:leave-start="opacity-100 transform scale-100" x-transition:leave-end="opacity-0 transform scale-90" x-on:click.away="imgModalSrc = ''''" class="p-2 fixed w-full h-100 inset-0 z-50 overflow-hidden flex justify-center items-center bg-black bg-opacity-75">\n		            <div @click.away="imgModal = ''''" class="flex flex-col max-w-3xl max-h-full overflow-auto">\n		              <div class="z-50">\n		                <button @click="imgModal = ''''" class="float-right pt-2 pr-2 outline-none focus:outline-none">\n		                  <svg class="fill-current text-white " xmlns="http://www.w3.org/2000/svg" width="18" height="18" viewBox="0 0 18 18">\n		                    <path d="M14.53 4.53l-1.06-1.06L9 7.94 4.53 3.47 3.47 4.53 7.94 9l-4.47 4.47 1.06 1.06L9 10.06l4.47 4.47 1.06-1.06L10.06 9z">\n		                    </path>\n		                  </svg>\n		                </button>\n		              </div>\n		              <div class="p-2">\n		                <img :alt="imgModalSrc" class="object-contain h-1/2-screen" :src="imgModalSrc">\n		                <p x-text="imgModalDesc" class="text-center text-white"></p>\n		              </div>\n		            </div>\n		          </div>\n		        </template>\n      		</div>\n      \n		    <div x-data="{}" class="px-10 py-10">\n		        <div class="flex -mx-2">\n		          <div class="w-1/6 px-2">\n		            <div class="bg-gray-400">\n		              <a @click="$dispatch(''img-modal'', {  imgModalSrc: ''img/img-1.jpg'', imgModalDesc: ''簡單說明'' })" class="cursor-pointer">\n		                <img alt="Placeholder" class="object-fit w-full" src="img/img-1.jpg">\n		              </a>\n		            </div>\n		          </div>\n		      \n		          <div class="w-1/6 px-2">\n		            <div class="bg-gray-400">\n		              <a @click="$dispatch(''img-modal'', {  imgModalSrc: ''img/img-2.jpg'', imgModalDesc: ''簡單說明'' })" class="cursor-pointer">\n		                <img alt="Placeholder" class="object-fit w-full" src="img/img-2.jpg">\n		              </a>\n		            </div>\n		          </div>\n		          \n		          <div class="w-1/6 px-2">\n		            <div class="bg-gray-400">\n		              <a @click="$dispatch(''img-modal'', {  imgModalSrc: ''img/img-3.jpg'', imgModalDesc: ''簡單說明'' })" class="cursor-pointer">\n		                <img alt="Placeholder" class="object-fit w-full" src="img/img-3.jpg">\n		              </a>\n		            </div>\n		          </div>\n		          <div class="w-1/6 px-2">\n		            <div class="bg-gray-400">\n		              <a @click="$dispatch(''img-modal'', {  imgModalSrc: ''img/img-4.jpg'', imgModalDesc: ''簡單說明'' })" class="cursor-pointer">\n		                <img alt="Placeholder" class="object-fit w-full" src="img/img-4.jpg">\n		              </a>\n		            </div>\n		          </div>\n		          <div class="w-1/6 px-2">\n		            <div class="bg-gray-400">\n		              <a @click="$dispatch(''img-modal'', {  imgModalSrc: ''img/img-5.jpg'', imgModalDesc: ''簡單說明'' })" class="cursor-pointer">\n		                <img alt="Placeholder" class="object-fit w-full" src="img/img-5.jpg">\n		              </a>\n		            </div>\n		          </div>\n		          <div class="w-1/6 px-2">\n		            <div class="bg-gray-400">\n		              <a @click="$dispatch(''img-modal'', {  imgModalSrc: ''img/img-6.jpg, imgModalDesc: ''簡單說明'' })" class="cursor-pointer">\n		                <img alt="Placeholder" class="object-fit w-full" src="img/img-6.jpg">\n		              </a>\n		            </div>\n		          </div>\n		        </div>\n		    </div>\n\n		</article>', 6, '2022-09-05 03:49:08', '2022-09-05 03:49:08', 99, NULL, NULL, 0, NULL),
(15, 'NL 01', NULL, NULL, '<div class="container mx-auto mt-3 mb-12 px-4 py-4 md:px-16 md:py-4 bg-white shadow-2xl">\n			<h1 class="mt-12 mb-1 text-center text-3xl font-extrabold leading-none text-gray-900 sm:mb-3">最新消息</h1>\n			<div class="flex flex-wrap mt-12">\n				<div class="grid grid-cols-1 gap-16 lg:grid-cols-2">\n					<div class="flex flex-col items-start justify-center h-full sm:flex-row sm:justify-start sm:text-left">\n						<img src="img/img-4.jpg" class="w-full md:w-48 h-auto rounded object-cover object-center flex-shrink-0 sm:mb-0 mb-6">\n						<div class="flex flex-wrap flex-col justify-between flex-auto md:pl-6">\n							<h3 class="flex-full font-semibold text-black mb-2"><a href="#">Google 修改搜尋演算法，未來排序將以行動優先</a></h3>\n							<time class="block text-xs text-gray-400"><i class="fa fa-clock mr-2" aria-hidden="true"></i>2022/6/6</time>\n							<p class="text-sm mt-3 leading-loose">Google 這幾年為提倡行動版內容，在搜尋中不斷提高行動版網頁的搜尋排序權重，近日再宣佈未來將逐步以行動版內容決定網站搜</p>\n						</div>\n					</div>\n					<div class="flex flex-col items-start justify-center h-full sm:flex-row sm:justify-start sm:text-left">\n						<img src="img/img-5.jpg" class="w-full md:w-48 h-auto rounded object-cover object-center flex-shrink-0 sm:mb-0 mb-6">\n						<div class="flex flex-wrap flex-col justify-between flex-auto pl-6">\n							<h3 class="flex-full font-semibold text-black mb-2"><a href="#">Google 建議您使用 RWD 響應式網頁設計</a></h3>\n							<time class="block text-xs text-gray-400"><i class="fa fa-clock mr-2" aria-hidden="true"></i>2022/6/6</time>\n							<p class="text-sm mt-3 leading-loose">Google 建議所有的網站使用 RWD 技術做為標準，讓網頁在不同的裝置顯示不同的結果 (依CSS)。有助於網站排名優化 (SEO)，故強</p>\n						</div>\n					</div>\n					<div class="flex flex-col items-start justify-center h-full sm:flex-row sm:justify-start sm:text-left">\n						<img src="img/img-6.jpg" class="w-full md:w-48 h-auto rounded object-cover object-center flex-shrink-0 sm:mb-0 mb-6">\n						<div class="flex flex-wrap flex-col justify-between flex-auto md:pl-6">\n				