INSERT INTO `webpages` (`id`, `title`, `user_id`, `imgurl`, `html`, `created_at`, `updated_at`, `sort_order`) VALUES
(1, '功能介紹', 2, '"jHEGenvETOcsmMWpVdv8KqzNAleJhm-metaUkVELmpwZw==-.jpg"', '<div class="feature-box">\n<dl class="feature-items">\n<dt><img src="img/icon-3.png" alt="" />\n<h3>首頁輪播圖</h3>\n</dt>\n<dd>\n<p>應用於首頁形象大圖輪播</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-2.png" alt="" />\n<h3>靜態頁</h3>\n</dt>\n<dd>\n<p>可放多圖、文字編排、youtube影片等..</p>\n<p>名稱參考：</p>\n<p>關於我們 / 營業項目 / 環境介紹 / 服務流程或產品詳細..等說明頁</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-7.png" alt="" />\n<h3>產品</h3>\n</dt>\n<dd>\n<p>名稱參考：</p>\n<p>產品介紹 / 商品介紹 / 產品應用 / 實績案例..等</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-6.png" alt="" />\n<h3>最新消息</h3>\n</dt>\n<dd>\n<p>名稱參考：</p>\n<p>活動消息 / 活動訊息 / 最新展覽..等</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-1.png" alt="" />\n<h3>後台管理者</h3>\n</dt>\n<dd>\n<p>可新增、編輯、刪除等..</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-4.png" alt="" />\n<h3>聯絡我們</h3>\n</dt>\n<dd>\n<p>前台留言後，於後台備份查看</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-5.png" alt="" />\n<h3>網站設定</h3>\n</dt>\n<dd>\n<p>聯絡資訊 / 指定收件人 / FB連結 & Line客服帳號欄位 / GA追蹤碼欄位 / SEO站內優化..等</p>\n</dd>\n</dl>\n<dl class="feature-items">\n<dt><img src="img/icon-8.png" alt="" />\n<h3>sitemap.xml</h3>\n</dt>\n<dd>\n<p>可用於提交至站長工具，幫助網站快速被蒐入</p>\n</dd>\n</dl>\n</div>', '2022-07-25 02:27:26', '2022-08-26 07:13:14', 1),
(2, '問與答', NULL, NULL, '<div class="faq-box">\n<details>\n<summary><h2><span>Q1</span>如何確認網站風格？</h2></summary>\n<div class="steps"><img src="img/screen-1.png" />\n<h3>挑選喜歡的風格款式</h3>\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2.png" />\n<h3>內頁風格確認OK後可告知<strong>編號</strong>（<em>橘色</em>）。</h3>\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-3.png" />\n<h3>頁面裡的區塊樣式可更換，請進入：<a href="../../../frame">區塊總覽</a> 。</h3>\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-4.png" />\n<h3>於左側選項 > 確認<strong>編號</strong>即可。</h3>\n`</div>`\n</details>\n\n<details>\n<summary><h2><span>Q2</span>如何取得Line帳號連結？[安卓]</h2></summary>\n<div class="steps"><img src="img/screen-2-1.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-2.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-3.jpg" />\n</div>\n</details>\n\n<details>\n<summary><h2><span>Q2</span>如何取得Line帳號連結？[IOS]</h2></summary>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-4.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-5.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-6.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-7.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-8.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-2-9.jpg" />\n</div>\n</details>\n\n<details>\n<summary><h2><span>Q3</span>如何放入Youtube影片？</h2></summary>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT01.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT02.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT03.jpg" />\n<h3 style="margin-top: 40px;margin-left: 30px;">如何<a href="#" target="_blank">讓影片自適應 》</a></h3>\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT04.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT05.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT06.jpg" />\n</div>\n</details>\n\n<details>\n<summary><h2><span>Q4</span>如何放入FB影片？</h2></summary>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-FB01.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-FB02.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-FB03.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-FB04.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-FB05.jpg" />\n</div>\n</details>\n\n<details>\n<summary><h2><span>Q5</span>如何讓Youtube影片自適應？</h2></summary>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-RWD01.jpg" />\n<h3><a href="#" target="_blank">http://embedresponsively.com/</a></h3>\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-RWD02.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-RWD03.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT04.jpg" />\n</div>\n<img class="down" src="img/icon-arrow-1.gif" />\n<div class="steps"><img src="img/screen-YT05.jpg" />\n</div>\n<img clas